        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Orkaitėje kepti burgeriukai ant kopūsto", "1. Orkaitę įkaitinti iki 200 C (režimas viršus+apačia be vėjelio).\n\n2. Kopūstą supjaustyti apie 2 cm storumo riekėmis.\n\n3. Kepimo formą iškloti kepimo popieriumi, išdėlioti kopūstą. Kiekvieną riekę pabarstyti su druska, pipirais ir apšlakstyti aliejumi.\n\n4. Kepti įkaitintoje iki 200 C orkaitėje apie 10 min., kol kiek apskrus.\n\n5. Tuo tarpu į dubenį dėti maltą jautieną, berti druską, pipirus, česnako miltelius, berti smulkintas petražoles ir gerai išminkyti.\n\n6. Apkeptus kopūstus išimti iš orkaitės. Kiekvieną aptepti 1 šaukštu pomidorų padažo.\n\n7. Iš maltos mėsos suformuoti paplotėlius (man gavosi 5 vienetai) ir dėti ant kopūstų riekių.\n\n8. Viską grąžinti į orkaitę ir kepti 10-15 min. (arba kol mėsa iškeps).\n\n9. Likus kelioms minutėms iki kepimo pabaigos, kiekvieną mėsainį pabarstyti tarkuotu sūriu.\n\n10. Patiekiant galima pabarstyti smulkintomis petražolėmis.\n\n", 30, 5, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kopūstų (į mažesnę pusę)", 0f, "1 vienetas"), Ingredient(0, 0, "maltos jautienos (naudojau 15% riebumo)", 0f, "500 gramų"), Ingredient(0, 0, "fermentinio sūrio (arba mocarelos)", 0f, "100 gramų"), Ingredient(0, 0, "pomidorų padažo", 0f, "5 šaukštai"), Ingredient(0, 0, "česnako miltelių", 0f, "1 šaukštelis"), Ingredient(0, 0, "petražolių", 0f, "1 sauja"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį"), Ingredient(0, 0, "alyvuogių aliejaus", 0f, "šiek tiek")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Bulvytės kremiškame sūrio padaže", "1. Bulves labai gerai nuplauti.\n\n2. Į puodą pilti vandens, berti druskos ir užvirti. Tuomet sumažinti kaitrą, sudėti bulves ir virti apie 13 minučių iki kol bus minkštos. Svarbu nepervirti, nes kitaip jos neišlaikys savo formos.\n\n3. Kol bulvės verda, gaminame padažą. Česnakus susmulkinti. Į gilesnę keptuvę dėti sviestą ir ištirpinti, sudėti česnaką ir pakepti apie 30 sekundžių, tuomet berti miltus ir apie minutę pakepti nuolat maišant. Pilti vištienos sultinį ir nuolat maišyti iki vientisos masės. Patogiausia tai daryti konditerine šluotele. Padažas turi pradėti tirštėti.\n\n4. Į keptuvę supilti grietinėlę, berti sūrį, pipirus, itališkas žoleles, jei trūksta, dar įberti druskos ir maišant pakaitinti iki kol sūris ištirps ir padažas sutiršės. Jei padažas atrodo per daug tirštas, įpilkite šlakelį sultinio.\n\n5. Išgriebti bulves ir sudėti į keptuvę su padažu, permaišyti, kad visos bulvytės pasidengtų padažu.\n\n", 20, 4, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "bulvių (naudojome mažas, bet tinka ir įprastos, žr. patarimus)", 0f, "750 gramų"), Ingredient(0, 0, "vištienos sultinio", 0f, "240 mililitrų"), Ingredient(0, 0, "grietinėlės (naudojau 30 % riebumo)", 0f, "200 mililitrų"), Ingredient(0, 0, "sviesto", 0f, "60 gramų"), Ingredient(0, 0, "fermentinio sūrio (intensyvesniam skoniui - kietojo sūrio)", 0f, "50 gramų"), Ingredient(0, 0, "česnako", 0f, "4 skiltelės"), Ingredient(0, 0, "kvietinių miltų", 0f, "2 šaukštai"), Ingredient(0, 0, "druskos", 0f, "1 šaukštelis"), Ingredient(0, 0, "itališkų žolelių (arba provanso žolelių)", 0f, "1 šaukštelis"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "1 žiupsnelis")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Vaikyste kvepiantis pyragas su uogomis ir ledais", "1. Sumaišome miltus, cukrų, kepimo miltelius. Mušame kiaušinius, pilame vanilės ekstraktą ir viską sumaišome rankomis.\n\n2. Dedame paruoštą tešlą į šaldytuvą valandai pailsėti. Po valandos, dalį tešlos dedame į pyrago apačią, gerai prispaudžiame pirštais, tuomet pilame uogienę ir sudedame likusią tešlos dalį.\n\n3. Ant viršaus pabarstome migdolų drožlių. Kepame įkaitintoje orkaitėje statiniu režimu 180 laipsnių apie 1 valandą.\n\n4. Pagaminame Philadelphia ledus: į plaktuvą dedame kreminį sūrį ir uogas, beriame cukraus ir pilame šaltos grietinėlės.\n\n5. Pyragą patiekiame su ledais.\n\n", 30, 8, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "uogų (mišinio, naudojau Beribu)", 0f, "600 gramų"), Ingredient(0, 0, "kvietinių miltų", 0f, "440 gramų"), Ingredient(0, 0, "kreminio sūrio (naudojau Philadelphia)", 0f, "200 gramų"), Ingredient(0, 0, "sviesto", 0f, "185 gramai"), Ingredient(0, 0, "kiaušinių", 0f, "2 vienetai"), Ingredient(0, 0, "cukraus", 0f, "100 gramų"), Ingredient(0, 0, "kepimo miltelių", 0f, "6 gramai"), Ingredient(0, 0, "vanilės ekstrakto", 0f, "pagal skonį"), Ingredient(0, 0, "grietinėlės (šaltos)", 0f, "pagal poreikį")), listOf())
