        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Žaliosios salotos su kietuoju sūriu - paruošiamos akimirksniu ir labai skanios", "1. Raminę salotą postambiai supjaustyti. Svogūnus supjaustyti plonais žiedais. Viską sudėti į dubenį.\n\n2. Ten pat berti smulkiai tarkuotą kietąjį sūrį.\n\n3. Padažui paruošti mažame dubenėlyje sumaišyti alyvuogių aliejų, citrinų sultis, pipirus ir paprikų dribsnius.\n\n4. Padažą užpilti ant salotų, gerai permaišyti ir iškart patiekti.\n\n", 5, 3, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "romaninių salotų (didesnės arba kitų mėgiamų salotų)", 0f, "1 gūžė"), Ingredient(0, 0, "kietojo sūrio (smulkiai tarkuoto)", 0f, "70 gramų"), Ingredient(0, 0, "šalotinių svogūnų (arba 1 rausvojo)", 0f, "2 vienetai"), Ingredient(0, 0, "alyvuogių aliejaus", 0f, "3 šaukštai"), Ingredient(0, 0, "citrinų sulčių", 0f, "1 šaukštas"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "1 žiupsnelis"), Ingredient(0, 0, "aitriųjų paprikų dribsnių (nebūtina)", 0f, "1 žiupsnelis")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Kreminė vištiena su špinatais", "1. Į keptuvę pilame alyvuogių aliejaus ir dedame kelias skilteles česnako. Beriame špinatus ir įpilame šlakelį vandens. Jiems apkepus, nukošiame ir leidžiame pastovėti.\n\n2. Vištienos filė supjaustome labai plonais gabalėliais. Įkaitiname keptuvę su alyvuogių aliejumi, česnaku ir dedame vištienos gabalėlius. Pilame balto vyno, įtarkuojame citrinos žievelės, beriame miltų ir dedame indelį Philadelphia kreminio sūrio. Viską gerai išmaišome.\n\n3. Špinatus pagardiname sviestu, rozmarinų druska ir parmezanu. Patiekiame kartu su kremine vištiena ir papuošiame šviežiu čiobreliu.\n\n", 30, 4, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "vištienos filė", 0f, "500 gramų"), Ingredient(0, 0, "kreminio sūrio (Philadelphia su česnakais)", 0f, "200 gramų"), Ingredient(0, 0, "špinatų", 0f, "100 gramų"), Ingredient(0, 0, "balto vyno", 0f, "100 mililitrų"), Ingredient(0, 0, "citrinos žievelės", 0f, "1 vienetas"), Ingredient(0, 0, "kvietinių miltų", 0f, "1 šaukštas"), Ingredient(0, 0, "česnako", 0f, "2 skiltelės"), Ingredient(0, 0, "alyvuogių aliejaus", 0f, "1 šlakelis"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį"), Ingredient(0, 0, "čiobrelių", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Didelis bulvinis blynas su šoninės ir sūrio įdaru", "1. Virtas bulves atvėsinkite iki kambario temperatūros, tuomet dėkite į šaldytuvą ir atšaldykite pernakt. Kitą dieną nulupkite odeles.\n\n2. Šaltas virtas bulves sutarkuokite burokine tarka ir sumaišykite su druska.\n\n3. Paruoškite įdarą. Keptuvėje virš vidutinio dydžio ugnies įkaitinkite alyvuogių aliejų. Suberkite smulkiai pjaustytą svogūną ir česnako skilteles, ir kepkite, kartais pamaišydami, kol suminkštės. Sudėkite gabalėliais pjaustytą šoninę ir kepkite dar porą minučių, kol gardžiai apskrus. Atvėsinkite, tuomet sumaišykite su tarkuotu sūriu.\n\n4. Iškepkite blyną. Į keptuvę įpilkite du šaukštus aliejaus ir įdėkite gabalėlį (apie 40 g) sviesto. Palaukite, kol sviestas ištirps.\n\n5. Sudėkite į keptuvę pusę tarkuotų bulvių. Lygiu sluoksniu padenkite visa keptuvės dugną. Paspauskite plokščiąja mentelės dalimi, kad bulvių tarkiai kiek įmanoma labiau sukibtų.\n\n6. Ant viršaus tolygiai paskleiskite šoninės ir sūrio įdarą. Pačiuose kraštuose įdaro nedėkite.\n\n7. Ant įdaro tolygiai paskleiskite likusias tarkuotas bulves, vėl paspauskite plokščiąja mentelės dalimi.\n\n8. Mentele atstumkite bulvių tarkius nuo kraštų, taip suformuodami gražios formos blyną. Kepkite apie 10-15 minučių, kol blyno apačia sutvirtės ir apskrus.\n\n9. Ant keptuvės užvožkite lentelę ar lėkštę ir apverskite – blynas liks ant lentelės ar lėkštėje, apskrudusia puse į viršų. Jei nerimaujate, kad vėliau jį bus sunku grąžinti atgal į keptuvę, pasinaudokite gudrybe – ant keptuvės užtieskite kepimo popieriaus juostą, kuri išvertus blyną, liks jo apačioje. Tuomet atgal į keptuvę blyną grąžinsite tempdami už kepimo popieriaus juostos.\n\n10. Į keptuvę įpilkite dar du šaukštus aliejaus, įdėkite dar gabaliuką sviesto. Palaukite, kol ištirps.\n\n11. Blyną nuo lentelės ar lėkštės nustumkite į keptuvę. Kepkite dar apie 10 minučių, kol ir antra blyno pusė sutvirtės ir bus gražiai apskrudusi.\n\n12. Patiekite. Iškepusį blyną pjaustykite gabalėliais ir patiekite vieną ar pagardinkite grietine, graikišku jogurtu.\n\n", 30, 3, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "bulvių (virtų su odele)", 0f, "1 kilogramas"), Ingredient(0, 0, "fermentinio sūrio (tarkuoto)", 0f, "100 gramų"), Ingredient(0, 0, "šaltai rūkytos šoninės", 0f, "100 gramų"), Ingredient(0, 0, "sviesto", 0f, "80 gramų"), Ingredient(0, 0, "svogūnų", 0f, "1 vienetas"), Ingredient(0, 0, "česnako", 0f, "3 skiltelės"), Ingredient(0, 0, "aliejaus", 0f, "4 šaukštai"), Ingredient(0, 0, "alyvuogių aliejaus", 0f, "2 šaukštai"), Ingredient(0, 0, "druskos", 0f, "1 šaukštelis")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Veganiški čirviniai blynai (be kiaušinių ir pieno produktų)", "1. Į dubenį berti miltus, kepimo miltelius, sodą, cukrų ir išmaišyti.\n\n2. Supilti migdolinį gėrimą, sukrėsti veganišką alternatyvą jogurtui, pilti ištirpintą kokosų aliejų ir vanilės ekstraktą. Gerai išmaišyti.\n\n3. Palikti tešlą pastovėti apie 10-15 min. Pastovėjusi tešla turėtų būti šiek tiek skystesnė už riebią grietinę. Jei atrodytų tirštoka, įpilkite dar migdolinio gėrimo, o jei per skysta - įdėkite dar truputį miltų.\n\n4. Įkaitinti čirvinių blynų keptuvę.\n\n5. Į keptuvę kiekvienam blynui pilti po samtelį paruoštos tešlos, užspausti ir kepti pagal savo keptuvės instrukcijas.\n\n6. Iškepusius blynus patiekite kaip mėgstate. Mes valgėme pagardinę augaline alternatyva jogurtui, aviečių uogiene ir uogomis.\n\n", 30, 10, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "cukraus (arba pagal skonį mėgiamo saldumo)", 0f, "4 šaukštai"), Ingredient(0, 0, "augalinio migdolinio gėrimo (naudojome „Naturli”)", 0f, "300 mililitrų"), Ingredient(0, 0, "kvietinių miltų (naudojome aukščiausios rūšies 550 C)", 0f, "300 gramų"), Ingredient(0, 0, "augalinės alternatyvos jogurtui", 0f, "200 gramų"), Ingredient(0, 0, "kokosų aliejaus (ištirpinto)", 0f, "70 gramų"), Ingredient(0, 0, "kepimo miltelių", 0f, "2 šaukšteliai"), Ingredient(0, 0, "sodos (nebūtina, bet blynai pavyks dar puresni, traškesni)", 0f, "0.5 šaukštelio"), Ingredient(0, 0, "vanilės ekstrakto", 0f, "1 šaukštelis")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Keptos kriaušės su pelėsiniu sūriu ir riešutais", "1. Orkaitę įjungti kaisti iki 200 C (kaitinimo režimas - viršus+apačia, be ventiliatoriaus).\n\n2. Kriaušes perpjauti pusiau, šaukšteliu išskobti sėklas ir aplinkinį minkštimą padarant didesnę įdubą, kad tilptų įdaras.\n\n3. Šiek tiek nupjauti kriaušių puselių apačios, kad geriau laikytųsi ir nevirstų. Sudėti į kepimo formą, įdubomis į viršų\n\n4. Glazūrai į nedidelį prikaistuvį dėti medų, sviestą, berti cinamoną ir išmaišyti. Pašildyti iki masė taps vientisa. Ja aptepti kriaušes.\n\n5. Įdarui į dubenėlį sutrupinti sūrį, berti peiliu pasmulkintus riešutus ir čiobrelių lapelius. Išmaišyti ir šį įdarą paskirstyti į kriaušių puseles.\n\n6. Kepti įkaitintos iki 200 C orkaitės viduryje apie 15-20 min iki kriaušės suminkštės ir šiek tiek apskrus.\n\n7. Iškepusias kriaušes apšlakstyti likusia glazūra, galima dar papuošti čiobrelių šakelėmis ir iškart tiekti.\n\n", 30, 6, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kriaušių (didesnių)", 0f, "3 vienetai"), Ingredient(0, 0, "sūrio su melsvuoju pelėsiu (naudojome VILVI Memel Blue)", 0f, "100 gramų"), Ingredient(0, 0, "graikinių riešutų", 0f, "100 gramų"), Ingredient(0, 0, "sviesto", 0f, "1 šaukštas"), Ingredient(0, 0, "medaus", 0f, "1 šaukštas"), Ingredient(0, 0, "malto cinamono", 0f, "0.5 šaukštelio"), Ingredient(0, 0, "švieži čiobreliai (arba trečdalio šaukštelio džiovintų)", 0f, "1 šaukštelis")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Kreminis brokolių ir pupelių troškinys su šonine", "1. Pasiruošti ingredientus: šoninę smulkiai supjaustyti kubeliais, brokoliui nupjauti kotą, o likusią dalį supjaustyti smulkesniais žiedynais, svogūną supjaustyti kubeliais, česnaką smulkiai sukapoti.\n\n2. Įkaitinti keptuvę. Suberti šoninę ir maišant kepti, kol gražiai apskrus.\n\n3. Iškepusią šoninę perkelti į lėkštę ir išpilti pusę susikaupusių riebalų.\n\n4. Į keptuvę su likusiais riebalais dėti sviestą, ištirpinti. Suberti svogūnus ir maišant kepti 3-4 min iki suminkštės. Sudėti brokolius maišant ir kepti 1-2 min. Įspausti česnaką ir maišant kepti apie 1 min.\n\n5. Sukrėsti gerai nuvarvintas pupeles, supilti grietinėlę. Sumažinti kaitrą ir troškinti apie 3–4 min.\n\n6. Dėti apkepintą šoninę, smulkiai tarkuotą kietąjį sūrį ir patroškinti dar apie 2-3 min. Pagal skonį pagardinti druska ir pipirais.\n\n7. Jei padažao norisi tištesnio, supilti krakmolą (prieš tai sumaišytą su 2 v.š. šalto vandens).\n\n", 30, 4, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "brokolių", 0f, "500 gramų"), Ingredient(0, 0, "konservuotų pupelių (naudojau baltas nedideles)", 0f, "1 skardinė"), Ingredient(0, 0, "grietinėlės, 30% riebumo", 0f, "200 mililitrų"), Ingredient(0, 0, "šaltai rūkytos šoninės", 0f, "100 gramų"), Ingredient(0, 0, "svogūnų", 0f, "1 vienetas"), Ingredient(0, 0, "česnako", 0f, "5 skiltelės"), Ingredient(0, 0, "kietojo sūrio (smulkiai tarkuoto, nebūtina)", 0f, "1 sauja"), Ingredient(0, 0, "sviesto", 0f, "1 šaukštas"), Ingredient(0, 0, "kukurūzų krakmolo (nebūtina)", 0f, "1.5 šaukštelio"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Crostini su kriaušėmis, kumpiu ir pelėsiniu sūriu", "1. Prancūzišką batoną supjaustyti įstrižomis riekelėmis. Jas iš abiejų pusių patepti alyvuogių aliejumi ir išdėlioti ant kepimo popieriumi išklotos skardos. Kepti iki 220C įkaitintos orkaitės viduryje 5-7 min iki švelniai paruduos. Kepdami stebėkite, nes tikslus laikas gali priklaustyti nuo orkaitės ir riekelių storumo.\n\n2. Rikotos sūrį sudėkite į dubenį. Į jį smulkia tarka įtarkuokite sūrį su melsvuoju pelėsiu. Išmaišykite.\n\n3. Kriaušę supjaustykite plonomis riekelėmis. Jas apšlakstykite trupučiu citrinos sulčių kad nepatamsuotų.\n\n4. Vytinto kumpio riekeles perpjaukite išilgai į 3-4 dalis, kad gautumėte juosteles. Jomis apvyniokite kriaušių gabalėlius.\n\n5. Ant kiekvienos batono riekelės užtepkite rikotos ir pelėsinio sūrio kremo. Dėkite kumpiu apvyniotą kriaušę. Pabarstykite trupučiu šviežiai maltų pipirų. Aš kartais dar mėgstu apšlakstyti balzamiko kremu, bet tas nebūtina.\n\n", 30, 20, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "prancūziško batono", 0f, "1 vienetas"), Ingredient(0, 0, "rikotos sūrio", 0f, "200 gramų"), Ingredient(0, 0, "sūrio su melsvuoju pelėsiu (naudojau VILVI Memel Blue)", 0f, "100 gramų"), Ingredient(0, 0, "kriaušių", 0f, "1 vienetas"), Ingredient(0, 0, "vytinto kumpio", 0f, "80 gramų"), Ingredient(0, 0, "citrinų", 0f, "0.5 vieneto"), Ingredient(0, 0, "alyvuogių aliejaus", 0f, "pagal poreikį"), Ingredient(0, 0, "pipirų", 0f, "šiek tiek")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Bulviniai blynai orkaitėje", "1. Orkaitę jungti kaisti iki 200° C (kaitinimo režimas - viršus+apačia, be ventiliatoriaus).\n\n2. Bulves nulupti, nuplauti, sutarkuoti per stambią tarką. Tarkius sudėti į kiaurasamtį ir labai gerai nuspausti skystį.\n\n3. Į bulvių masę įtarkuoti svogūną. Berti česnako miltelius, pagal skonį druskos ir pipirų, gerai išmaišyti.\n\n4. Įmušti kiaušinius, išmaišyti. Berti miltus ir dar kartą išmaišyti. Svarbu - reikiamas miltų kiekis priklausys nuo bulvių rūšies, jų dėti tiek, kad masė liktų drėgna, bet būtų lengvai formuojama.\n\n5. Kepimo skardą dosniai pašlakstyti aliejumi. Imti golfo kamuoliuko dydžio masės kiekį ir rankomis suformuoti plokščią blyną. Dėti ant skardos.\n\n6. Blynus kepti iš pradžių apatinėje orkaitės lentynoje apie 10 min (arba kol apskrus apačia). Tuomet blynus apversti, pakelti į orkaitės viršų ir pakepti dar apie 10 min (arba iki apskrus ir viršus).\n\n7. Šiuos bulvinius blynus tinka patiekti su grietine ar jogurtu. Patiekdami galite pabarstyti smulkintais žalumynais (petražolėmis, krapais ar kt.)\n\n", 30, 3, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "bulvių", 0f, "500 gramų"), Ingredient(0, 0, "kiaušinių (M dydžio)", 0f, "2 vienetai"), Ingredient(0, 0, "svogūnų (nedidelio)", 0f, "1 vienetas"), Ingredient(0, 0, "kvietinių miltų (apytikriai)", 0f, "3 šaukštai"), Ingredient(0, 0, "česnako miltelių", 0f, "1 šaukštelis"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį"), Ingredient(0, 0, "aliejaus", 0f, "pagal poreikį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Laksa su krevetėmis", "1. Išverdame makaronus ir juos atidedame.\n\n2. Puode arba keptuvėje įkaitiname aliejų, dedame krevetes, kepame kelias minutes.\n\n3. Į puodą ar keptuvę įtarkuojame česnaką ir apie 2 cm. imbiero šaknies, dedame pievagrybius ir kepiname viską dar kelias minutes.\n\n4. Į puodą ar keptuvę dedame raudonojo kario pastą. Kepiname viską dar minutę.\n\n5. Puodo ar keptuvės turinį užpilame sultiniu, leidžiame jam užvirti. Kai sultinys užverda, supilame kokosų pieną, įspaudžiame laimo sultis.\n\n6. Paragaujame ir pridedame druskos ir pipirų.\n\n7. Į dubenėlius dedame makaronų, užpilame juos paruošta sriuba.\n\n8. Patiekiame su šviežiu baziliku, kalendra, aitriosios paprikos griežinėliais ir sezamo sėklomis.\n\n", 30, 4, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "sultinio", 0f, "1 litras"), Ingredient(0, 0, "kokosų pieno (naudojome \"Santa Maria\")", 0f, "400 gramų"), Ingredient(0, 0, "krevečių", 0f, "200 gramų"), Ingredient(0, 0, "ramen makaronai (naudojome \"Santa Maria\")", 0f, "200 gramų"), Ingredient(0, 0, "pievagrybių", 0f, "8 vienetai"), Ingredient(0, 0, "žaliųjų citrinų", 0f, "2 vienetai"), Ingredient(0, 0, "česnako", 0f, "4 skiltelės"), Ingredient(0, 0, "raudonosios kario pastos (naudojome \"Santa Maria\")", 0f, "2 šaukštai"), Ingredient(0, 0, "imbiero (šviežio)", 0f, "2 centimetrai"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "pipirų", 0f, "pagal skonį"), Ingredient(0, 0, "kokosų aliejaus", 0f, "pagal skonį"), Ingredient(0, 0, "baziliko lapelių (šviežių)", 0f, "pagal skonį"), Ingredient(0, 0, "kalendros (šviežios)", 0f, "pagal skonį"), Ingredient(0, 0, "aitriosios paprikos", 0f, "pagal skonį"), Ingredient(0, 0, "sezamo sėklų (juodųjų)", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Nugarinės kepsniai su kreminiu viskio padažu", "1. Į keptuvę dedame 2 v. š. sviesto. Kai sviestas išsilydo ir keptuvė gerai įkaista, dedame kambario temperatūros kepsnius ir kepame ant didelės kaitros 8 minutes, kas minutę apversdami. Jei norite pilnai iškepusių steikų, kepkite 12 minučių.\n\n2. Iškepusius kepsnius perkeliame į lėkštę, pabarstome druska ir pipirais bei leidžiame pailsėti kambario temperatūroje, kol paruošime padažą.\n\n3. Ruošiame padažą: keptuvėje išlydome likusį sviestą, sudedame kubeliais pjaustytą svogūną ir kepiname kelias minutes. Įtarkuojame česnaką ir kepiname dar minutę. Supilame viskį ir pakaitiname apie minutę, kol alkoholis išgaruoja. Sudedame garstyčias, Vorčesterio padažą, grietinėlę, kaitiname maišydami kelias minutes, kol padažas sutirštėja.\n\n4. Kepsnius grąžiname į keptuvę su padažu, patiekiant galima pabarstyti šviežiomis žolelėmis. Patiekite su mėgstamais garnyrais, tobula kombinacija būtų šilkinė bulvių košė ir keptos šparaginės pupelės šalia.\n\n", 20, 2, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "jautienos nugarinės kepsnių (naudojome „Feel the Beef“ marmurinės jautienos nugarinės kepsnius)", 0f, "2 vienetai"), Ingredient(0, 0, "grietinėlės, 35% riebumo", 0f, "100 mililitrų"), Ingredient(0, 0, "sviesto", 0f, "60 gramų"), Ingredient(0, 0, "svogūnų", 0f, "1 vienetas"), Ingredient(0, 0, "česnako", 0f, "2 skiltelės"), Ingredient(0, 0, "viskio (arba brendžio ar burbono)", 0f, "2 šaukštai"), Ingredient(0, 0, "dižono garstyčių", 0f, "1 šaukštas"), Ingredient(0, 0, "vorčesterio padažo", 0f, "1 šaukštelis"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį"), Ingredient(0, 0, "žalumynų", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Kondensuoto pieno padažas - tobulas blyneliams ir kitiems desertams", "1. Į prikaistuvį supilti kondensuotą pieną, dėti sviestą ir pilti vandenį.\n\n2. Masę išmaišyti ir kaitinti ant vidutinės kaitros, kol sviestas išsilydys ir masė taps vientisa.\n\n3. Į paruoštą masę pilti vanilės ekstraktą, išmaišyti, nukelti puodą nuo kaitros.\n\n4. ​\n\n", 30, 6, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kondensuoto pieno, saldinto", 0f, "400 gramų"), Ingredient(0, 0, "vandens (arba pagal poreikį,karšto)", 0f, "50 mililitrų"), Ingredient(0, 0, "sviesto", 0f, "50 gramų"), Ingredient(0, 0, "vanilės ekstrakto", 0f, "2 šaukšteliai"), Ingredient(0, 0, "druskos", 0f, "1 žiupsnelis")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Burokėlių ir mocarelos salotos ala Caprese", "1. Burokėlius galima naudoti virtus, bet man dar skaniau su keptais orkaitėje. Juos ruošiu taip - kiekvieną burokėlį (nenuluptą) švariai nuplaunu ir suvynioju į foliją. Kepu 180-200° C temperatūros orkaitėje apie 1 - 1.5 val. iki suminkštėja (dažniausiai kepu tuo pačiu, kai orkaitėje gaminu kažką kitą).\n\n2. Iškepusius burokėlius nulupti ir supjaustyti vienodo storio griežinėliais.\n\n3. Mocarelos sūrį supjaustyti griežinėliais.\n\n4. Į lėkštę dėti burokėlio griežinėlį, tuomet mocarelą, vėl burokėlį ir taip, kol neliks ingredientų. Dėti atsargiai, nes burokėlis dažo :)\n\n5. Salotas pabarstyti druska ir pipirais. Apšlakstyti alyvuogių aliejumi, balzaminiu actu, užberti šviežių ar džiovintų čiobrelių. Tiekti iškart.\n\n", 20, 2, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "burokėlių (vidutinio dydžio, naudojau keptus orkaitėje, bet tinka ir virti, žr. patarimus)", 0f, "3 vienetai"), Ingredient(0, 0, "mocarela sūrio (apie 250 gramų)", 0f, "2 vienetai"), Ingredient(0, 0, "alyvuogių aliejaus", 0f, "1 šaukštas"), Ingredient(0, 0, "balzaminio acto (naudojau tirštą)", 0f, "1 šaukštas"), Ingredient(0, 0, "švieži čiobreliai (arba šiek tiek džiovintų čiobrelių ar raudonėlio)", 0f, "1 saujelė"), Ingredient(0, 0, "druskos", 0f, "šiek tiek"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "šiek tiek")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Tandoori vištienos iešmeliai karšto oro gruzdintuvėje (arba orkaitėje)", "1. Medinius iešmelius pamerkti į vandenį ir palikti bent 10 - 15 min (kad kepant neimtų svilti).\n\n2. Vištieną supjaustyti vidutinio dydžio gabalėliais ir sudėti į dubenį.\n\n3. Dėti graikišką jogurtą, įspausti česnaką, įtarkuoti imbierą, suberti visus prieskonius bei druską, gerai išmaišyti. Paruoštą mėsą galima kepti iš karto, bet jei turite laiko, palikite marinuotis bent valandą (ar iš vakaro) - bus dar skaniau.\n\n4. Ant iešmelių suverti vištienos gabalėlius, kad būtų prigludę vienas prie kito, be tarpų.\n\n5. Iešmelius dėti į gruzdintuvės indą (galima kepti ir orkaitėje, žr. patarimus žemiau) ir kepti 200 C laipsnių temperatūroje 12 min. Tada iešmelius apverskti ir kepti dar 12 min arba iki vištiena bus gražiai apskrudusi ir iškepusi viduje.\n\n6. Iešmelius galite patiekti su savo mėgiamu padažu. Aš padažui sumaišiau graikišką jogurtą su smulkintomis mėtomis, svogūno laiškais ir šlakeliu citrinos sulčių.\n\n", 20, 8, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "vištienos šlaunelių mėsos (be kaulų ir odos)", 0f, "800 gramų"), Ingredient(0, 0, "graikiško jogurto (arba paprasto natūralaus jogurto)", 0f, "200 gramų"), Ingredient(0, 0, "česnako (arba pagal skonį)", 0f, "2 vienetai"), Ingredient(0, 0, "imbiero (arba 1 šaukštelio imbiero miltelių)", 0f, "1 centimetras"), Ingredient(0, 0, "maltos ciberžolės", 0f, "1 šaukštelis"), Ingredient(0, 0, "rūkytos paprikos miltelių (arba saldžios paprikos miltelių)", 0f, "1 šaukštelis"), Ingredient(0, 0, "maltos kalendros", 0f, "1 šaukštelis"), Ingredient(0, 0, "druskos (arba pagal skonį)", 0f, "1 šaukštelis"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "1 šaukštelis"), Ingredient(0, 0, "maltų kajeno pipirų (arba aitriosios paprikos miltelių)", 0f, "0.5 šaukštelio")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Sotus ir maistingas mėsos troškinys su daržovėmis", "1. Į keptuvę įpilame aliejaus ir dedame česnako skilteles. Aliejui įkaitus, išimame česnaką ir dedame pjaustytą vištieną gerai apkepti.\n\n2. Mėsai apkepus, beriame smulkiai pjaustytą salierą, morką ir svogūną. Viskam kelias minutes pasitroškinus, beriame iš vakaro užmerktas kruopas: speltą, perlines, avižas ir žirnius. Įpilame vandens ir patroškiname 12 minučių.\n\n3. Po to suberiame pupeles ir leidžiame dar pasitroškinti 10 minučių. Troškinį pagardiname Philadelphia kreminiu sūriu, druska, pipirais, šviežiu čiobreliu ir alyvuogių aliejumi.\n\n", 30, 4, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "vištienos filė", 0f, "400 gramų"), Ingredient(0, 0, "avižinių dribsnių", 0f, "100 gramų"), Ingredient(0, 0, "perlinių kruopų", 0f, "100 gramų"), Ingredient(0, 0, "morkų", 0f, "1 vienetas"), Ingredient(0, 0, "salierų stiebų", 0f, "1 vienetas"), Ingredient(0, 0, "svogūnų", 0f, "1 vienetas"), Ingredient(0, 0, "česnako", 0f, "2 skiltelės"), Ingredient(0, 0, "alyvuogių aliejaus", 0f, "1 šlakelis"), Ingredient(0, 0, "čiobrelių šakelių", 0f, "1 šakelė"), Ingredient(0, 0, "kreminio sūrio (naudojau Philadelphia)", 0f, "pagal poreikį"), Ingredient(0, 0, "speltų miltų", 0f, "pagal poreikį"), Ingredient(0, 0, "žirnių", 0f, "pagal skonį"), Ingredient(0, 0, "baltų pupelių", 0f, "pagal poreikį"), Ingredient(0, 0, "raudonų pupelių", 0f, "pagal poreikį"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Traškios česnakinės bulvytės orkaitėje", "1. Įjungti orkaitę kaisti iki 200 C temperatūros (kepimo režimas - viršus+apačia+ventiliatorius).\n\n2. Neskustas bulves švariai nuplauti ir supjaustyti nedideliais kubeliais (lupti nereikia).\n\n3. Bulves suberti į puodą, pilti vandens kad apsemtų, įberti druskos, užvirti ir virti apie 5-10 minučių, kol bulvės kiek suminkštės, bet neišvirs.\n\n4. Bulvėms apvirus, vandenį nupilti ir bulves gerai nusausinti.\n\n5. Didelę kepimo skardą iškloti kepimo popieriumi, sukrėsti bulves ir paskirstyti, kad jos būtų vienu sluoksniu. Apšlakstyti aliejumi, pabarstyti druska bei pipirais. Dėti į iki 200C įkaitintos orkaitės vidurį ir kepti 10-15 minučių arba iki bulvės gražiai apskrus.\n\n6. Kol bulvės kepa, paruošti česnakinį sviestą. Nulupti ir smulkiai supjaustyti česnaką. Sviestą dėti į keptuvę, ištirpinti ant silpnos kaitros, suberti česnaką. Česnakai turi suminkštėti, tačiau nesusvilti (visas šis procesas trunka apie 10 minučių). Kai česnakai suminkštės, nukelti keptuvę nuo kaitros, atidėti.\n\n7. Išimti bulves iš orkaitės. Į česnakinį sviestą suberti smulkiai pjaustytas petražoles, išmaišyti. Paruoštą masę tolygiai supilti ant bulvyčių, išmaišyti.\n\n", 45, 4, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "bulvių (didesnių)", 0f, "5 vienetai"), Ingredient(0, 0, "sviesto", 0f, "60 gramų"), Ingredient(0, 0, "česnako", 0f, "8 skiltelės"), Ingredient(0, 0, "petražolių", 0f, "1 sauja"), Ingredient(0, 0, "alyvuogių aliejaus", 0f, "2 šaukštai"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Įdaryti kiaušiniai su avokadais (be majonezo)", "1. Kiaušinius perpjauti išilgai pusiau. Išimti trynius.\n\n2. Avokadą perpjauti pusiau, išimti kauliuką, išimti minkštimą.\n\n3. Avokadą su citrinos sultimis sutrinti šakute. Dėti kiaušinių trynius, garstyčius, pagardinti druska ir pipirais,dar kartą pertrinti šakute.\n\n4. Svogūnų laiškus ir krapus smulkiai sukapoti ir sumaišyti su avokado mase.\n\n5. Įdaryti kiaušinių baltymų puseles ir iš karto patiekti. Skanaus!\n\n", 10, 8, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kiaušinių (kietai virtų)", 0f, "4 vienetai"), Ingredient(0, 0, "avokadų (gerai prinokusio)", 0f, "1 vienetas"), Ingredient(0, 0, "citrinų sulčių", 0f, "2 šaukštai"), Ingredient(0, 0, "garstyčių (iš stiklainiuko)", 0f, "1 šaukštelis"), Ingredient(0, 0, "svogūnų laiškų (nedidelės)", 0f, "1 saujelė"), Ingredient(0, 0, "krapų (nedidelės)", 0f, "1 saujelė"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Šviežiais burokėliais natūraliai dažyti margučiai", "1. Burokėlius nuploviau ir sutarkavau (tarkavau su visomis žievelėmis, skusti nebūtina). Nuo tarkavimo stambumo priklausys raštas, kurį paliks burokėliai ant margučio. Kuo smulkiau tarkuosite, tuo vientisesnis raštas bus ir margutis greičiau nusidažys. Nuotraukoje esančiam rašui išgauti aš pusę burokėlių tarkavau stambiau (vadinama “burokine” tarka), o pusę - smulkia tarka. Tada abi tarkuotas mases sumaišiau tarpusavyje, supyliau actą ir vėl išmaišiau.\n\n2. Ėmiau virtą kiaušinį (naudojau baltu lukštu), jį nestoru sluoksniu apdėliojau tarkuotais burokėliais, taip kad niekur neliktų tarpelių ir nesimatytų lukšto, standžiai apspaudžiau ir šį kiaušinį “burokėlių patale” įvyniojau į celofaninį maišelį (tinka ir maistinė plėvėlė). Šio burokėlių kiekio man užteko 6 kiaušiniams.\n\n3. Suvyniotus kiaušinius padėjau į šaldytuvą. Pirmoje nuotraukoje esantį margutį laikiau parą, kadangi norėjau stipresnės spalvos (palaikius per naktį spalva ir raštas jau buvo, bet tokie gana švelnūs).\n\n4. Tada margutį išvyniojau ir labai atsargiai, švelniai nuėmiau burokėlių likučius. Jokiu būdu margučio neplaukite, nes kol jis neišdžiūvęs, spalva nužsifiksavusi, tad plaudami galite pašalinti dažo plėvelę. Nesirūpinkite jeigu liks prilipęs vienas kitas burokėlio trupinukas, jie tik paįvairins raštą :)\n\n5. Margučius padėjau ant popierinių rankšluostėlių ir palikau nudžiūti (jeigu skubate, džiovinimą galite paspartinti plaukų džiovintuvu). Nudžiūvusius margučius švelniai pablizginau truputyje aliejaus suvilgyta medžiagos skiautele, taip spalva dar labiau išryškėjo. Tik blizginimui nenaudokite kokosų aliejaus, nes jis suteikia balzganumo.\n\n", 20, 6, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kiaušinių (virtų, baltu lukštu)", 0f, "6 vienetai"), Ingredient(0, 0, "burokėlių (vidutinio dydžio, nevirtų)", 0f, "4 vienetai"), Ingredient(0, 0, "acto 9%", 0f, "2 šaukštai")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Vištienos ir kiaušinių užkandėlė \"Musmirės\"", "1. Sutarkuokite smulkiai kiaušinius ir sūrį. Supjaustykite mažais kubeliais vištieną. Susmulkinkite česnako skilteles.\n\n2. Įdėkite majonezo ir viską sumaišykite.\n\n3. Agurką supjaustykite 0.5 cm storio griežinėliais. Pomidoriukus perpjaukite pusiau.\n\n4. Išdėliokite ant lėkštės žalumynus, sudėkite supjaustytus agurkų griežinėlius.\n\n5. Iš kumpio, kiaušinių, sūrio ir majonezo masės suformuokite nedidelius rutuliukus (bus grybų koteliai). Vieną rutuliuką dėkite ant agurko griežinėlio, taip sudėkite visus rutuliukus.\n\n6. Ant viršaus uždėkite pomidorų puseles ir jas papuoškite majonezo taškeliais.\n\n", 30, 30, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "vyšninių pomidorų", 0f, "15 vienetų"), Ingredient(0, 0, "ilgavaisių agurkų", 0f, "1 vienetas"), Ingredient(0, 0, "kiaušinių (kietai virtų)", 0f, "3 vienetai"), Ingredient(0, 0, "rūkytų vištienos šlaunelių (ar kitos mėsos)", 0f, "120 gramų"), Ingredient(0, 0, "fermentinio sūrio", 0f, "100 gramų"), Ingredient(0, 0, "majonezo (apytikriai)", 0f, "1.5 šaukšto"), Ingredient(0, 0, "česnako", 0f, "2 skiltelės"), Ingredient(0, 0, "žalumynų (salotų lapų, krapų, svogūnų laiškų arba petražolių)", 0f, "šiek tiek")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Įdaryti kiaušiniai su avokadais (be majonezo)", "1. Kiaušinius perpjauti išilgai pusiau. Išimti trynius.\n\n2. Avokadą perpjauti pusiau, išimti kauliuką, išimti minkštimą.\n\n3. Avokadą su citrinos sultimis sutrinti šakute. Dėti kiaušinių trynius, garstyčius, pagardinti druska ir pipirais,dar kartą pertrinti šakute.\n\n4. Svogūnų laiškus ir krapus smulkiai sukapoti ir sumaišyti su avokado mase.\n\n5. Įdaryti kiaušinių baltymų puseles ir iš karto patiekti. Skanaus!\n\n", 10, 8, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kiaušinių (kietai virtų)", 0f, "4 vienetai"), Ingredient(0, 0, "avokadų (gerai prinokusio)", 0f, "1 vienetas"), Ingredient(0, 0, "citrinų sulčių", 0f, "2 šaukštai"), Ingredient(0, 0, "garstyčių (iš stiklainiuko)", 0f, "1 šaukštelis"), Ingredient(0, 0, "svogūnų laiškų (nedidelės)", 0f, "1 saujelė"), Ingredient(0, 0, "krapų (nedidelės)", 0f, "1 saujelė"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį")), listOf())
