        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Bulvių užkandėlė su burokėliais ir silke", "1. Bulves supjaustyti maždaug 0.5 - 1 cm storio griežinėliais. Iš abiejų pusių aptepti aliejumi, pabarstyti druska ir pipirais. Vienu sluoksniu dėti į kepimo popieriumi išklotą skardą.\n\n2. Kepti įkaitintoje iki 200 C laipsnių temperatūros orkaitėje su vėjeliu apie 10 minučių iki kol krašteliai švelniai paruduos, o viršus taps traškesnis. Tuomet ištraukti iš orkaitės ir atidėti atvėsti.\n\n3. Burokėlius sutarkuoti per burokinę tarką. Svogūną smulkiai supjaustyti ir gerai sumaišyti su burokėliais. Įdėti majonezo, pagardinti druska ir dar kartą permaišyti.\n\n4. Silkę supjaustyti vieno kąsnio gabalėliais.\n\n5. Ant bulvės griežinėlio dėti maždaug arbatinį šaukštelį paruoštos burokėlių masės, tuomet uždėti silkės gabalėlį.\n\n", 45, 20, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "bulvių (vidutinio dydžio, išvirtų su lupenomis)", 0f, "4 vienetai"), Ingredient(0, 0, "virtų burokėlių", 0f, "250 gramų"), Ingredient(0, 0, "silkių filė (naudojau be aliejaus)", 0f, "100 gramų"), Ingredient(0, 0, "svogūnų (vidutinio dydžio)", 0f, "1 vienetas"), Ingredient(0, 0, "majonezo (naudojau veganišką be kiaušinių, bet tinka ir klasikinis)", 0f, "2 šaukštai"), Ingredient(0, 0, "alyvuogių aliejaus", 0f, "pagal poreikį"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Vaikyste kvepiantis pyragas su uogomis ir ledais", "1. Sumaišome miltus, cukrų, kepimo miltelius. Mušame kiaušinius, pilame vanilės ekstraktą ir viską sumaišome rankomis.\n\n2. Dedame paruoštą tešlą į šaldytuvą valandai pailsėti. Po valandos, dalį tešlos dedame į pyrago apačią, gerai prispaudžiame pirštais, tuomet pilame uogienę ir sudedame likusią tešlos dalį.\n\n3. Ant viršaus pabarstome migdolų drožlių. Kepame įkaitintoje orkaitėje statiniu režimu 180 laipsnių apie 1 valandą.\n\n4. Pagaminame Philadelphia ledus: į plaktuvą dedame kreminį sūrį ir uogas, beriame cukraus ir pilame šaltos grietinėlės.\n\n5. Pyragą patiekiame su ledais.\n\n", 30, 8, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "uogų (mišinio, naudojau Beribu)", 0f, "600 gramų"), Ingredient(0, 0, "kvietinių miltų", 0f, "440 gramų"), Ingredient(0, 0, "kreminio sūrio (naudojau Philadelphia)", 0f, "200 gramų"), Ingredient(0, 0, "sviesto", 0f, "185 gramai"), Ingredient(0, 0, "kiaušinių", 0f, "2 vienetai"), Ingredient(0, 0, "cukraus", 0f, "100 gramų"), Ingredient(0, 0, "kepimo miltelių", 0f, "6 gramai"), Ingredient(0, 0, "vanilės ekstrakto", 0f, "pagal skonį"), Ingredient(0, 0, "grietinėlės (šaltos)", 0f, "pagal poreikį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Itališkas tiramisu pagal Gian Lucą", "1. Trynius išsukame su cukrumi, kol pabals. Įmaišome maskarponę ir atidedame į šalį. Iki standumo išplakame baltymus ir įmaišome į maskarponės ir trynių masę.\n\n2. Pradedame formuoti tiramisu: dedame kremą, į kavą trumpam įmerkiame sausainius, klojame ant kremo, vėl pagardiname kremu ir pabarstome kakavos. Visa tai kartojame ir užbaigiame kremo ir kakavos sluoksniu\n\n", 30, 8, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kiaušinių trynių", 0f, "5 vienetai"), Ingredient(0, 0, "kiaušinių baltymų", 0f, "5 vienetai"), Ingredient(0, 0, "maskarponės sūrio", 0f, "250 gramų"), Ingredient(0, 0, "cukraus", 0f, "5 šaukštai"), Ingredient(0, 0, "kakavos", 0f, "pagal skonį"), Ingredient(0, 0, "biskvitinių pirštelių", 0f, "pagal poreikį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Žiedinio kopūsto ir rūkytos vištienos salotos", "1. Įkaitinti sausą keptuvę, suberti saulėgrąžas ir pakepti apie 3 minutes vis pamaišant (iki pasklis malonus aromatas).\n\n2. Žiedinį kopūstą supjaustyti smulkiais žiedynais, porą - griežinėliais, svogūnų laiškus susmulkinti.\n\n3. Vištų ketvirčių mėsą nupjauti nuo kaulų, nuimti odeles. Mėsą supjaustyti nedideliais kubeliais.\n\n4. Į dubenį dėti žiedinį kopūstą, vištieną, porą, svogūno laiškus, lukštentas saulėgrąžas, dėti majonezo, pagardinti druska, pipirais ir gerai išmaišyti.\n\n", 10, 6, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "žiedinių kopūstų (nedidelio)", 0f, "1 vienetas"), Ingredient(0, 0, "karštai rūkytų vištų ketvirčių (svoris dar su kaulais ir odelėmis)", 0f, "600 gramų"), Ingredient(0, 0, "poro", 0f, "1 vienetas"), Ingredient(0, 0, "majonezo (arba pagal skonį, gaivesnei versijai galima keisti jogurtu)", 0f, "3 šaukštai"), Ingredient(0, 0, "saulėgrąžų (nebūtina, bet labai tinka)", 0f, "1 sauja"), Ingredient(0, 0, "svogūnų laiškų", 0f, "1 pundelis"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Naminis tinginys", "1. Sausainius rankomis sutrupiname giliame inde.\n\n2. Puode virš vidutinės ugnies ištirpiname sviestą. Į jį supilame kondensuotą pieną bei maišydami suberiame (o dar geriau - įsijojame) kakavą. Užviriname ir maišydami pakaitiname keletą minučių iki masė taps vientisa bei siek tiek sutirštės.\n\n3. Gautą masę užpilame ant sausainių ir šaukštu labai gerai išmaišome. Visi sausainiai turi būti daugmaž tolygiai pasidengę kakavine mase.\n\n4. Masę verčiame ant kepimo popieriaus ir spausdami tvirtu maišeliu formuojame norimo dydžio ir formos tinginį. Spaudžiame standžiai, kad neliktų tarpų.\n\n5. Leidžiame tinginiui atvėsti iki kambario temperatūros, o tada suvyniojame į maistę plėvelę arba kepimo poopierių. Dedame į šaldytuvą bent 6-8 valandoms (dar geriau - nakčiai), kad sustingtų, susigertų.\n\n", 30, 20, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "sausainių (naudojau \"Gaidelis\" klasikinius)", 0f, "400 gramų"), Ingredient(0, 0, "kondensuoto pieno, saldinto (apie 400g dydžio)", 0f, "1 skardinė"), Ingredient(0, 0, "sviesto", 0f, "200 gramų"), Ingredient(0, 0, "kakavos", 0f, "5 šaukštai")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Silkė su kiaušiniais, porais ir žirneliais", "1. Silkės filė ir kiaušinius supjaustykite kubeliais, porą supjaustykite plonais pusžiedžiais, žirnelius nuvarvinkite.\n\n2. Viską sudėkite į dubenį, dėkite majonezą ir gerai išmaišykite.\n\n3. Užberkite smulkintų svogūno laiškų.\n\n", 20, 2, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "silkių filė", 0f, "3 vienetai"), Ingredient(0, 0, "kiaušinių (virtų)", 0f, "3 vienetai"), Ingredient(0, 0, "poro", 0f, "1 vienetas"), Ingredient(0, 0, "konservuotų žirnelių", 0f, "100 gramų"), Ingredient(0, 0, "majonezo", 0f, "2 šaukštai"), Ingredient(0, 0, "svogūnų laiškų", 0f, "pagal poreikį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Grietinėlėje dažyti įvairiaspalviai margučiai", "1. Grietinėlę išplakti iki standumo (patogiausia - elektriniu plaktuvu).\n\n2. Kepimo skardoje (tinka esanti prie orkaitės) paskleisti grietinėlę ir švelniai mentele paskirstyti per visą plotį.\n\n3. Ant paruoštos grietinėlės pašlakstyti įvairių spalvų dažų (aš panaudojau 4 skirtingų spalvų skystų dažų maišelius). Mediniu pagaliuku sukant ratus švelniai maišyti dažus vienus su kitais ir paskirstyti juos taip, kad visas grietinėlės sluoksnis būtų išmargintas tarsi vaivorykštė, baltų vietų negali likti.\n\n4. Užsidėti pirštines (kitaip nusidažysite ir rankas:). Imti vieną kiaušinį, dėti ant grietinėlės ir laikant už smailiųjų kiaušinio kraštų jį apvolioti iki visas bus padengtas spalvota grietinėle. Tuomet atsargiai kilstelėti, kad dažų priliptų ir prie kiaušinio galų. Apvoliotą kiaušinį palikti skardoje ant grietinėlės.\n\n5. Tada imti kitą kiaušinį ir jį vėl apvolioti grietinėlėje. Apvoliotą palikti skardoje.\n\n6. Taip išmarginus visus kiaušinius.\n\n7. Su visa skarda dėti juos į šaldytuvą pastovėti bent 30 min.\n\n8. Vėl užsidėti pirštines ir nuplauti margučius po tekančiu vandeniu. Sudėti ant popierinių rankšluosčių ir palikti visiškai nudžiūti.\n\n", 45, 10, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kiaušinių (baltu lukštu)", 0f, "10 vienetų"), Ingredient(0, 0, "grietinėlės, 35% riebumo", 0f, "400 gramų"), Ingredient(0, 0, "skystų dažų kiaušiniams (skirtingų spalvų, galima naudoti ir dažus milteliais, žr. patarimus)", 0f, "pagal poreikį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Įdaryti kiaušiniai su avokadais (be majonezo)", "1. Kiaušinius perpjauti išilgai pusiau. Išimti trynius.\n\n2. Avokadą perpjauti pusiau, išimti kauliuką, išimti minkštimą.\n\n3. Avokadą su citrinos sultimis sutrinti šakute. Dėti kiaušinių trynius, garstyčius, pagardinti druska ir pipirais,dar kartą pertrinti šakute.\n\n4. Svogūnų laiškus ir krapus smulkiai sukapoti ir sumaišyti su avokado mase.\n\n5. Įdaryti kiaušinių baltymų puseles ir iš karto patiekti. Skanaus!\n\n", 10, 8, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kiaušinių (kietai virtų)", 0f, "4 vienetai"), Ingredient(0, 0, "avokadų (gerai prinokusio)", 0f, "1 vienetas"), Ingredient(0, 0, "citrinų sulčių", 0f, "2 šaukštai"), Ingredient(0, 0, "garstyčių (iš stiklainiuko)", 0f, "1 šaukštelis"), Ingredient(0, 0, "svogūnų laiškų (nedidelės)", 0f, "1 saujelė"), Ingredient(0, 0, "krapų (nedidelės)", 0f, "1 saujelė"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Šviežiais burokėliais natūraliai dažyti margučiai", "1. Burokėlius nuploviau ir sutarkavau (tarkavau su visomis žievelėmis, skusti nebūtina). Nuo tarkavimo stambumo priklausys raštas, kurį paliks burokėliai ant margučio. Kuo smulkiau tarkuosite, tuo vientisesnis raštas bus ir margutis greičiau nusidažys. Nuotraukoje esančiam rašui išgauti aš pusę burokėlių tarkavau stambiau (vadinama “burokine” tarka), o pusę - smulkia tarka. Tada abi tarkuotas mases sumaišiau tarpusavyje, supyliau actą ir vėl išmaišiau.\n\n2. Ėmiau virtą kiaušinį (naudojau baltu lukštu), jį nestoru sluoksniu apdėliojau tarkuotais burokėliais, taip kad niekur neliktų tarpelių ir nesimatytų lukšto, standžiai apspaudžiau ir šį kiaušinį “burokėlių patale” įvyniojau į celofaninį maišelį (tinka ir maistinė plėvėlė). Šio burokėlių kiekio man užteko 6 kiaušiniams.\n\n3. Suvyniotus kiaušinius padėjau į šaldytuvą. Pirmoje nuotraukoje esantį margutį laikiau parą, kadangi norėjau stipresnės spalvos (palaikius per naktį spalva ir raštas jau buvo, bet tokie gana švelnūs).\n\n4. Tada margutį išvyniojau ir labai atsargiai, švelniai nuėmiau burokėlių likučius. Jokiu būdu margučio neplaukite, nes kol jis neišdžiūvęs, spalva nužsifiksavusi, tad plaudami galite pašalinti dažo plėvelę. Nesirūpinkite jeigu liks prilipęs vienas kitas burokėlio trupinukas, jie tik paįvairins raštą :)\n\n5. Margučius padėjau ant popierinių rankšluostėlių ir palikau nudžiūti (jeigu skubate, džiovinimą galite paspartinti plaukų džiovintuvu). Nudžiūvusius margučius švelniai pablizginau truputyje aliejaus suvilgyta medžiagos skiautele, taip spalva dar labiau išryškėjo. Tik blizginimui nenaudokite kokosų aliejaus, nes jis suteikia balzganumo.\n\n", 20, 6, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kiaušinių (virtų, baltu lukštu)", 0f, "6 vienetai"), Ingredient(0, 0, "burokėlių (vidutinio dydžio, nevirtų)", 0f, "4 vienetai"), Ingredient(0, 0, "acto 9%", 0f, "2 šaukštai")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Purus varškės apkepas", "1. Manų kruopas užpilti pienu ir pabrinkinti, kol jų prireiks (jeigu vietoje manų kruopų naudotumėte dribsnius, darykite lygiai tą patį).\n\n2. Sviestą ištrinti su cukrumi. Atskirti kiaušinių baltymus nuo trynių ir trynius sudėti į sviesto-cukraus mišinį. Gerai išmaišyti, sudėti varškę ir vėl maišyti iki vientisos masės.\n\n3. Išplakti kiaušinių baltymus taip, kad apvertus indą jie liktų vietoje (labai svarbu, kad indas, kuriame plaksite būtų sausas, kitaip baltymai gali nesustandėti). Manų kruopas su pienu supilti į varškės masę ir išmaišyti. Tada sudėti išplaktus baltymus ir lengvai pamaišyti - labai svarbu nepermaišyti, kad nesukristų.\n\n4. Kepimo formą patepti sviestu arba aliejumi ir pabarstyti džiūvėsėliais. Masę sukrėsti į indą ir kepti 220 C laipsnių orkaitėje, kol iškils. Tuomet sumažinti ugnį iki 200 C laipsnių. Iš viso kepti reikėtų apie 30 minučių. Skanaus!\n\n", 30, 6, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "varškės", 0f, "500 gramų"), Ingredient(0, 0, "kiaušinių (M dydžio)", 0f, "4 vienetai"), Ingredient(0, 0, "pieno", 0f, "80 mililitrų"), Ingredient(0, 0, "manų kruopų (arba ryžių dribsnių, ar avižinių dribsnių)", 0f, "4 šaukštai"), Ingredient(0, 0, "sviesto", 0f, "30 gramų"), Ingredient(0, 0, "cukraus (arba medaus, ar kito mėgiamo saldiklio )", 0f, "2 šaukštai"), Ingredient(0, 0, "džiūvėsėlių", 0f, "šiek tiek")), listOf())
