        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Grietinėlėje dažyti įvairiaspalviai margučiai", "1. Grietinėlę išplakti iki standumo (patogiausia - elektriniu plaktuvu).\n\n2. Kepimo skardoje (tinka esanti prie orkaitės) paskleisti grietinėlę ir švelniai mentele paskirstyti per visą plotį.\n\n3. Ant paruoštos grietinėlės pašlakstyti įvairių spalvų dažų (aš panaudojau 4 skirtingų spalvų skystų dažų maišelius). Mediniu pagaliuku sukant ratus švelniai maišyti dažus vienus su kitais ir paskirstyti juos taip, kad visas grietinėlės sluoksnis būtų išmargintas tarsi vaivorykštė, baltų vietų negali likti.\n\n4. Užsidėti pirštines (kitaip nusidažysite ir rankas:). Imti vieną kiaušinį, dėti ant grietinėlės ir laikant už smailiųjų kiaušinio kraštų jį apvolioti iki visas bus padengtas spalvota grietinėle. Tuomet atsargiai kilstelėti, kad dažų priliptų ir prie kiaušinio galų. Apvoliotą kiaušinį palikti skardoje ant grietinėlės.\n\n5. Tada imti kitą kiaušinį ir jį vėl apvolioti grietinėlėje. Apvoliotą palikti skardoje.\n\n6. Taip išmarginus visus kiaušinius.\n\n7. Su visa skarda dėti juos į šaldytuvą pastovėti bent 30 min.\n\n8. Vėl užsidėti pirštines ir nuplauti margučius po tekančiu vandeniu. Sudėti ant popierinių rankšluosčių ir palikti visiškai nudžiūti.\n\n", 45, 10, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kiaušinių (baltu lukštu)", 0f, "10 vienetų"), Ingredient(0, 0, "grietinėlės, 35% riebumo", 0f, "400 gramų"), Ingredient(0, 0, "skystų dažų kiaušiniams (skirtingų spalvų, galima naudoti ir dažus milteliais, žr. patarimus)", 0f, "pagal poreikį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Žiedinio kopūsto ir rūkytos vištienos salotos", "1. Įkaitinti sausą keptuvę, suberti saulėgrąžas ir pakepti apie 3 minutes vis pamaišant (iki pasklis malonus aromatas).\n\n2. Žiedinį kopūstą supjaustyti smulkiais žiedynais, porą - griežinėliais, svogūnų laiškus susmulkinti.\n\n3. Vištų ketvirčių mėsą nupjauti nuo kaulų, nuimti odeles. Mėsą supjaustyti nedideliais kubeliais.\n\n4. Į dubenį dėti žiedinį kopūstą, vištieną, porą, svogūno laiškus, lukštentas saulėgrąžas, dėti majonezo, pagardinti druska, pipirais ir gerai išmaišyti.\n\n", 10, 6, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "žiedinių kopūstų (nedidelio)", 0f, "1 vienetas"), Ingredient(0, 0, "karštai rūkytų vištų ketvirčių (svoris dar su kaulais ir odelėmis)", 0f, "600 gramų"), Ingredient(0, 0, "poro", 0f, "1 vienetas"), Ingredient(0, 0, "majonezo (arba pagal skonį, gaivesnei versijai galima keisti jogurtu)", 0f, "3 šaukštai"), Ingredient(0, 0, "saulėgrąžų (nebūtina, bet labai tinka)", 0f, "1 sauja"), Ingredient(0, 0, "svogūnų laiškų", 0f, "1 pundelis"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Pikantiškas varškės apkepas su žalumynais", "1. Virš vidutinio dydžio ugnies ištirpinkite sviestą. Suberkite smulkintą svogūną ir česnako skilteles ir kepkite, kartais pamaišydami, kol suminkštės. Suberkite pasmulkintus špinatų lapelius ir kepkite dar minutę, kol suminkštės ir sukris. Nuimkite nuo ugnies ir atidėkite į šalį, kol atvės.\n\n2. Į didelį dubenį sudėkite varškę, tarkuotą kietąjį sūrį ir tarkuotą mocarelą, grietinę. Įmuškite kiaušinius, pagardinkite druska ir pipirais. Gerai išsukite, kol visi produktai susimaišys.\n\n3. Į masę sudėkite atvėsusius keptus svogūnus, česnakus ir špinatus, smulkiai supjaustytus svogūnų laiškus ir krapus. Dar kartą gerai išmaišykite.\n\n4. Orkaitę įkaitinkite iki 180 °C temperatūros (kaitinimas iš viršaus ir apačios).\n\n5. Paruoštą masę supilkite į kepimo indą (apie 22 cm skersmens). Kepkite įkaitintoje orkaitėje apie 60 minučių, kol apkepas gražiai iškils, apskrus ir bus iškepęs.\n\n6. Dešimt minučių atvėsinkite kepimo inde (apkepas šiek tiek subliukš – nieko tokio), tuomet pjaustykite gabalėliais ir patiekite. Valgykite vieną, su grietine ar su šviežių daržovių salotomis.\n\n", 30, 6, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "varškės 9%", 0f, "700 gramų"), Ingredient(0, 0, "kiaušinių", 0f, "4 vienetai"), Ingredient(0, 0, "mocarela sūrio (tarkuoto)", 0f, "125 gramai"), Ingredient(0, 0, "grietinės (riebios)", 0f, "125 gramai"), Ingredient(0, 0, "špinatų", 0f, "100 gramų"), Ingredient(0, 0, "kietojo sūrio (tarkuoto)", 0f, "100 gramų"), Ingredient(0, 0, "svogūnų", 0f, "1 vienetas"), Ingredient(0, 0, "česnako", 0f, "3 skiltelės"), Ingredient(0, 0, "krapų", 0f, "1 pundelis"), Ingredient(0, 0, "svogūnų laiškų", 0f, "1 pundelis"), Ingredient(0, 0, "sviesto", 0f, "2 šaukštai"), Ingredient(0, 0, "druskos", 0f, "1 šaukštelis"), Ingredient(0, 0, "grūstų juodųjų pipirų", 0f, "0.5 šaukštelio")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Greitos krevečių salotos su sūriu", "1. Krevetes, jei naudojate šaldytas, atitirpinti, gerai nuvarvinti ir sudėti į dubenį.\n\n2. Agurką, sūrį ir kiaušinius supjaustyti vidutinio dydžio kubeliais. Berti ant krevečių.\n\n3. Pagal skonį pagardinti majonezu, įspausti truputį citrinų sulčių, berti žiupsnelį pipirų ir išmaišyti. Skanaus!\n\n", 20, 4, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "krevečių be kevalo (naudojau šaldytas virtas, tinka ir iš sūrymo)", 0f, "200 gramų"), Ingredient(0, 0, "sūrio (mėgiamo, naudojau VILVI Memel Prussia)", 0f, "200 gramų"), Ingredient(0, 0, "ilgavaisių agurkų", 0f, "1 vienetas"), Ingredient(0, 0, "kiaušinių (kietai virtų)", 0f, "3 vienetai"), Ingredient(0, 0, "citrinų sulčių (nebūtina, bet labai tinka)", 0f, "1 šlakelis"), Ingredient(0, 0, "juodųjų pipirų", 0f, "1 žiupsnelis"), Ingredient(0, 0, "majonezo (kokybiško arba žr. patarimus)", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Krevetės toskanietiškame grietinėlės padaže", "1. Jei krevetės šaldytos, pirmiausia atitirpiname. Atitirpusias pabarstome trupučiu druskos.\n\n2. Pasiruošiame kitus ingredientus - svogūną smulkiai supjaustome, česnaką smulkiai sukapojame, saulėje džiovintus pomidorus gerai nuvarviname nuo aliejaus ir supjaustome plonomis išilgomis juostelėmis. Kietąjį sūrį smulkiai sutarkuojame. Špinatus nuplauname ir gerai nuvarviname.\n\n3. Keptuvėje įštirpiname daugmaž du trečdalius sviesto. Beriame česnaką, maišydami pakepiname apie 30s. Dedame krevetes ir pakepame po 1-2 min iš abiejų pusių, kad taptų rožinės. Išimame iš keptuvės, atidedame į lėkštę ar dubenį ir uždengiame, kad neatvėstų.\n\n4. Į keptuvę dedame likusį sviestą, beriame svogūną ir maišydami pakepiname 3-4 min ant vidutinės kaitros iki svogūnas suminkštės.\n\n5. Pilame baltą sausą vyną (jei nenaudojate, šį žingsnį praleiskite). Maišydami pakaitiname iki vynas beveik nugaruos.\n\n6. Dedame saulėje džiovintų pomidorų juosteles ir maišydami pakepiname 1-2 min, kad pomidorai paleistų skonio.\n\n7. Pilame grietinėlę, sultinį, beriame provanso žoleles. Pakaitiname 1-2 minutes, kad viskas sušiltų.\n\n8. Dedame špinatus ir maišydami pakaitiname iki jie susileis. Beriame tarkuotą sūrį ir išmaišome. Pagardiname žiupsneliu pipirų. Paragaujame, ar padažui pakanka sūrumo. Jei norisi, įberiame papildomai.\n\n9. Galiausiai sudedame krevetes ir pakaitiname 1-2 minutes, kad viskas sušiltų.\n\n", 30, 4, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "krevečių be kevalo (naudojau šaldytas)", 0f, "400 gramų"), Ingredient(0, 0, "grietinėlės, 35% riebumo (tinka ir liesa, žr. patarimus)", 0f, "200 mililitrų"), Ingredient(0, 0, "sultinio (naudojau daržovių)", 0f, "200 mililitrų"), Ingredient(0, 0, "špinatų", 0f, "100 gramų"), Ingredient(0, 0, "saulėje džiovintų pomidorų (svoris nusunktų nuo aliejaus, galima keisti šviežiais pomidorais, žr. patarimus)", 0f, "100 gramų"