        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Įdaryti kiaušiniai su avokadais (be majonezo)", "1. Kiaušinius perpjauti išilgai pusiau. Išimti trynius.\n\n2. Avokadą perpjauti pusiau, išimti kauliuką, išimti minkštimą.\n\n3. Avokadą su citrinos sultimis sutrinti šakute. Dėti kiaušinių trynius, garstyčius, pagardinti druska ir pipirais,dar kartą pertrinti šakute.\n\n4. Svogūnų laiškus ir krapus smulkiai sukapoti ir sumaišyti su avokado mase.\n\n5. Įdaryti kiaušinių baltymų puseles ir iš karto patiekti. Skanaus!\n\n", 10, 8, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "kiaušinių (kietai virtų)", 0f, "4 vienetai"), Ingredient(0, 0, "avokadų (gerai prinokusio)", 0f, "1 vienetas"), Ingredient(0, 0, "citrinų sulčių", 0f, "2 šaukštai"), Ingredient(0, 0, "garstyčių (iš stiklainiuko)", 0f, "1 šaukštelis"), Ingredient(0, 0, "svogūnų laiškų (nedidelės)", 0f, "1 saujelė"), Ingredient(0, 0, "krapų (nedidelės)", 0f, "1 saujelė"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį")), listOf())
        recipesRepository.insertRecipeWithIngredientsAndTags(Recipe(0, "Indiškas avinžirnių troškinys", "1. Avinžirnius sudėti į puodą, užpilti 400 ml vandens, pasūdyti. Užvirti ir nuimti nuo kaitros.\n\n2. Smulkiai supjaustyti svogūnus, smulkiai sutarkuoti imbierą, susmulkinti aitriuosius pipirus.\n\n3. Keptuvėje įkaitinti aliejų, jame apkepinti svogūnus, įspausti česnaką, imbierą, aitriuosius pipirus, smulkintą papriką ir morką ir maišydami viską pakepkite 8-10 min. ant nedidelės ugnies.\n\n4. Įmaišyti prieskonius ir pakepti 2-3 min., maišant, kol pasklis malonus kvapas. Jeigu ima svilti, įpilkite šlakelį vandens, kuriame mirksta avinžirniai.\n\n5. Sudėti konservuotus arba kubeliais pjaustytus šviežius pomidorus, įpilti dar šlakelį vandens, kuriame mirksta avinžirniai. Pakepti 5 min. vis maišydami ir šaukštu patrindami pomidorus.\n\n6. Sudėkite avinžirnius su likusiu vandeniu, kuriame jie mirksta. Troškinkite dar 10-15 min. iki kol viskas sutirštės iki jums patinkančios konsistencijos.\n\n", 45, 6, 0, false, Uri.EMPTY), listOf(Ingredient(0, 0, "konservuotų avinžirnių (ar 500 g virtų)", 0f, "800 gramų"), Ingredient(0, 0, "konservuotų pomidorų (arba 3 didelių, šviežių, prinokusių pomidorų)", 0f, "400 gramų"), Ingredient(0, 0, "morkų", 0f, "1 vienetas"), Ingredient(0, 0, "paprikos", 0f, "1 vienetas"), Ingredient(0, 0, "svogūnų", 0f, "3 vienetai"), Ingredient(0, 0, "česnako", 0f, "3 skiltelės"), Ingredient(0, 0, "imbiero", 0f, "5 centimetrai"), Ingredient(0, 0, "aitriosios paprikos (ar pagal skonį)", 0f, "2 vienetai"), Ingredient(0, 0, "maltos ciberžolės", 0f, "1 šaukštelis"), Ingredient(0, 0, "garam masala prieskonių", 0f, "1 šaukštelis"), Ingredient(0, 0, "kalendros", 0f, "0.5 šaukštelio"), Ingredient(0, 0, "malto kumino", 0f, "0.5 šaukštelio"), Ingredient(0, 0, "aitriosios paprikos miltelių", 0f, "0.5 šaukštelio"), Ingredient(0, 0, "druskos", 0f, "pagal skonį"), Ingredient(0, 0, "maltų juodųjų pipirų", 0f, "pagal skonį"), Ingredient(0, 0, "aliejaus (arba sviesto)",